
<map>
  <node ID="root" TEXT="费曼学习法">
    <node TEXT="何为“费曼学习法”" ID="s8UuEq1xMH" _mubu_text="%3Cspan%3E%E4%BD%95%E4%B8%BA%E2%80%9C%E8%B4%B9%E6%9B%BC%E5%AD%A6%E4%B9%A0%E6%B3%95%E2%80%9D%3C/span%3E" STYLE="bubble" POSITION="default">
      <node TEXT="费曼学习法提供了四个关键词：Concept（概念）；Teach（以教代学）；Review（评价）；Simplify（简化）。" ID="9OqL4AFWqM" _mubu_text="%3Cspan%3E%E8%B4%B9%E6%9B%BC%E5%AD%A6%E4%B9%A0%E6%B3%95%E6%8F%90%E4%BE%9B%E4%BA%86%E5%9B%9B%E4%B8%AA%E5%85%B3%E9%94%AE%E8%AF%8D%EF%BC%9AConcept%EF%BC%88%E6%A6%82%E5%BF%B5%EF%BC%89%EF%BC%9BTeach%EF%BC%88%E4%BB%A5%E6%95%99%E4%BB%A3%E5%AD%A6%EF%BC%89%EF%BC%9BReview%EF%BC%88%E8%AF%84%E4%BB%B7%EF%BC%89%EF%BC%9BSimplify%EF%BC%88%E7%AE%80%E5%8C%96%EF%BC%89%E3%80%82%3C/span%3E" STYLE="fork"/>
      <node TEXT="在这个基础上，本书提炼总结出了五个步骤：确立目标；理解目标；输出；回顾；简化。通过这五个步骤，我们能够充分地将费曼“以教代学”的学习方式开发到最高的效率，吸收有用的知识，创造自己的知识体系。" ID="OgBAUsmjcl" _mubu_text="%3Cspan%3E%E5%9C%A8%E8%BF%99%E4%B8%AA%E5%9F%BA%E7%A1%80%E4%B8%8A%EF%BC%8C%E6%9C%AC%E4%B9%A6%E6%8F%90%E7%82%BC%E6%80%BB%E7%BB%93%E5%87%BA%E4%BA%86%E4%BA%94%E4%B8%AA%E6%AD%A5%E9%AA%A4%EF%BC%9A%E7%A1%AE%E7%AB%8B%E7%9B%AE%E6%A0%87%EF%BC%9B%E7%90%86%E8%A7%A3%E7%9B%AE%E6%A0%87%EF%BC%9B%E8%BE%93%E5%87%BA%EF%BC%9B%E5%9B%9E%E9%A1%BE%EF%BC%9B%E7%AE%80%E5%8C%96%E3%80%82%E9%80%9A%E8%BF%87%E8%BF%99%E4%BA%94%E4%B8%AA%E6%AD%A5%E9%AA%A4%EF%BC%8C%E6%88%91%E4%BB%AC%E8%83%BD%E5%A4%9F%E5%85%85%E5%88%86%E5%9C%B0%E5%B0%86%E8%B4%B9%E6%9B%BC%E2%80%9C%E4%BB%A5%E6%95%99%E4%BB%A3%E5%AD%A6%E2%80%9D%E7%9A%84%E5%AD%A6%E4%B9%A0%E6%96%B9%E5%BC%8F%E5%BC%80%E5%8F%91%E5%88%B0%E6%9C%80%E9%AB%98%E7%9A%84%E6%95%88%E7%8E%87%EF%BC%8C%E5%90%B8%E6%94%B6%E6%9C%89%E7%94%A8%E7%9A%84%E7%9F%A5%E8%AF%86%EF%BC%8C%E5%88%9B%E9%80%A0%E8%87%AA%E5%B7%B1%E7%9A%84%E7%9F%A5%E8%AF%86%E4%BD%93%E7%B3%BB%E3%80%82%3C/span%3E" STYLE="fork"/>
    </node>
    <node TEXT="高效能的“费曼学习法”" ID="kRcTAm9jcy" _mubu_text="%3Cspan%3E%E9%AB%98%E6%95%88%E8%83%BD%E7%9A%84%E2%80%9C%E8%B4%B9%E6%9B%BC%E5%AD%A6%E4%B9%A0%E6%B3%95%E2%80%9D%3C/span%3E" STYLE="bubble" POSITION="default">
      <node TEXT="第一，确立你要学习的目标。找到和列出自己想要了解的知识，可以是一本书，也可以是一门技术，甚至是你能想象到的任意领域和事物。" ID="yP4broWb4k" _mubu_text="%3Cspan%3E%E7%AC%AC%E4%B8%80%EF%BC%8C%E7%A1%AE%E7%AB%8B%E4%BD%A0%E8%A6%81%E5%AD%A6%E4%B9%A0%E7%9A%84%E7%9B%AE%E6%A0%87%E3%80%82%E6%89%BE%E5%88%B0%E5%92%8C%E5%88%97%E5%87%BA%E8%87%AA%E5%B7%B1%E6%83%B3%E8%A6%81%E4%BA%86%E8%A7%A3%E7%9A%84%E7%9F%A5%E8%AF%86%EF%BC%8C%E5%8F%AF%E4%BB%A5%E6%98%AF%E4%B8%80%E6%9C%AC%E4%B9%A6%EF%BC%8C%E4%B9%9F%E5%8F%AF%E4%BB%A5%E6%98%AF%E4%B8%80%E9%97%A8%E6%8A%80%E6%9C%AF%EF%BC%8C%E7%94%9A%E8%87%B3%E6%98%AF%E4%BD%A0%E8%83%BD%E6%83%B3%E8%B1%A1%E5%88%B0%E7%9A%84%E4%BB%BB%E6%84%8F%E9%A2%86%E5%9F%9F%E5%92%8C%E4%BA%8B%E7%89%A9%E3%80%82%3C/span%3E" STYLE="fork">
        <node TEXT="目标明确的学习可以极大地改变一个人的思维，对于训练和改进我们的思维方式而言，这是一个必不可少的基础。它主要体现在四个方面" ID="BRdRlTHuko" _mubu_text="%3Cspan%3E%E7%9B%AE%E6%A0%87%E6%98%8E%E7%A1%AE%E7%9A%84%E5%AD%A6%E4%B9%A0%E5%8F%AF%E4%BB%A5%E6%9E%81%E5%A4%A7%E5%9C%B0%E6%94%B9%E5%8F%98%E4%B8%80%E4%B8%AA%E4%BA%BA%E7%9A%84%E6%80%9D%E7%BB%B4%EF%BC%8C%E5%AF%B9%E4%BA%8E%E8%AE%AD%E7%BB%83%E5%92%8C%E6%94%B9%E8%BF%9B%E6%88%91%E4%BB%AC%E7%9A%84%E6%80%9D%E7%BB%B4%E6%96%B9%E5%BC%8F%E8%80%8C%E8%A8%80%EF%BC%8C%E8%BF%99%E6%98%AF%E4%B8%80%E4%B8%AA%E5%BF%85%E4%B8%8D%E5%8F%AF%E5%B0%91%E7%9A%84%E5%9F%BA%E7%A1%80%E3%80%82%E5%AE%83%E4%B8%BB%E8%A6%81%E4%BD%93%E7%8E%B0%E5%9C%A8%E5%9B%9B%E4%B8%AA%E6%96%B9%E9%9D%A2%3C/span%3E" STYLE="fork">
