          <node TEXT="第三，逻辑性思维。" ID="HFIRDaIo9w" _mubu_text="%3Cspan%3E%E7%AC%AC%E4%B8%89%EF%BC%8C%E9%80%BB%E8%BE%91%E6%80%A7%E6%80%9D%E7%BB%B4%E3%80%82%3C/span%3E" STYLE="fork"/>
          <node TEXT="第四，清晰凝练的表达力。" ID="XUxnzkIces" _mubu_text="%3Cspan%3E%E7%AC%AC%E5%9B%9B%EF%BC%8C%E6%B8%85%E6%99%B0%E5%87%9D%E7%BB%83%E7%9A%84%E8%A1%A8%E8%BE%BE%E5%8A%9B%E3%80%82%3C/span%3E" STYLE="fork"/>
        </node>
